// app/screens/Home/Postagens.jsx
import React from 'react';
import { View, SafeAreaView, Text, ScrollView, TouchableOpacity, Image } from 'react-native';
import styles from './styles'; // Reutilizando o estilo do projeto

const posts = [
  {
    id: 1,
    imagem: require('../../../assets/images/capaPost.png'),
    titulo: 'Como a Tecnologia Pode Facilitar o Aprendizado',
    conteudo: 'A tecnologia trouxe inúmeras ferramentas que facilitam o aprendizado dos alunos, tornando as aulas mais dinâmicas.',
    data: '21/10/2024',
    autor: 'Professor Carlos'
  },
  {
    id: 2,
    imagem: require('../../../assets/images/capaPost.png'),
    titulo: 'Estratégias de Ensino para a Era Digital',
    conteudo: 'No mundo digital, os professores precisam adaptar suas estratégias de ensino para capturar a atenção dos alunos.',
    data: '22/10/2024',
    autor: 'Professor Beatriz'
  },
  {
    id: 3,
    imagem: require('../../../assets/images/capaPost.png'),
    titulo: 'A Importância do Feedback na Educação',
    conteudo: 'O feedback dos professores pode ajudar os alunos a melhorarem seu desempenho e entenderem seus erros.',
    data: '23/10/2024',
    autor: 'Professor Eduardo'
  },
  // Adicione mais posts conforme necessário
];

export default function Postagens() {
  return (
    <SafeAreaView style={styles.safeArea}>
      <View style={styles.headerContainer}>
        <Text style={styles.titulo}>Postagens Recentes</Text>
      </View>

      <ScrollView contentContainerStyle={styles.scrollContainer}>
        {posts.map((post, index) => (
          <TouchableOpacity key={index} style={styles.postContainer}>
            <Image style={styles.capaPost} source={post.imagem} />
            <View style={styles.infoPost}>
              <Text style={styles.tituloPost}>{post.titulo}</Text>
              <Text style={styles.resumoPost}>{post.conteudo}</Text>
              <View style={styles.infoPostRodape}>
                <Text style={styles.data}>{post.data}</Text>
                <Text style={styles.autor}>{post.autor}</Text>
              </View>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
    </SafeAreaView>
  );
}
