// app/navigation/NavegacaoTab.jsx
import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Inicio from '../screens/Home/Inicio';
import Entrar from '../screens/Home/Entrar';
import ListaProfessores from '../screens/Home/ListaProfessores'; // Novo componente para listagem de professores
import Perfil from '../screens/Home/Perfil/Perfil'; // Corrigido o caminho para o componente Perfil
import Postagens from '../screens/Home/Postagens'; // Novo componente para exibir todas as postagens
import Admin from '../screens/Admin/Admin'; // Novo componente para gerenciar posts, acessível apenas para professores
import CriarPostagem from '../screens/Admin/CriarPostagem'; // Novo componente para criar postagens, acessível apenas para professores
import TabBarIcon from '../../components/TabBar/TabIcon'; // Componente para renderizar ícones personalizados da TabBar

const Tab = createBottomTabNavigator();

export default function NavegacaoTab({ user }) {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: false,
        tabBarShowLabel: true, // Mostrar label para dar contexto aos ícones
        tabBarLabelStyle: {
          fontSize: 12,
          marginBottom: 4, // Ajuste para alinhar visualmente o label
        },
        tabBarActiveTintColor: '#FBED51',
        tabBarInactiveTintColor: '#fff',
        tabBarStyle: {
          backgroundColor: '#2579E7',
          borderTopWidth: 1,
          borderTopColor: '#2579E7',
          height: 65,
          paddingBottom: 5, // Garantir alinhamento vertical dos ícones
        },
      }}
    >
      <Tab.Screen
        name="Home"
        component={Inicio}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/home.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Início',
        }}
      />
      <Tab.Screen
        name="Postagens"
        component={Postagens}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/file-text.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Postagens',
        }}
      />
      <Tab.Screen
        name="Professores"
        component={ListaProfessores}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/users.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Professores',
        }}
      />
      <Tab.Screen
        name="Perfil"
        component={Perfil} // Ajustado o caminho do componente
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/user.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Perfil',
        }}
      />
      <Tab.Screen
        name="Login"
        component={Entrar}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/key.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Login',
        }}
      />
      {/* Aba disponível apenas para professores logados */}
      {user?.isProfessor && (
        <>
          <Tab.Screen
            name="Admin"
            component={Admin}
            options={{
              tabBarIcon: ({ color, focused }) => (
                <TabBarIcon
                  icon={require('../../assets/icons/settings.png')}
                  color={color}
                  focused={focused}
                />
              ),
              tabBarLabel: 'Administração',
            }}
          />
          <Tab.Screen
            name="CriarPostagem"
            component={CriarPostagem}
            options={{
              tabBarIcon: ({ color, focused }) => (
                <TabBarIcon
                  icon={require('../../assets/icons/plus.png')}
                  color={color}
                  focused={focused}
                />
              ),
              tabBarLabel: 'Criar Postagem',
            }}
          />
        </>
      )}
    </Tab.Navigator>
  );
}
