import React, { useState } from 'react';
import { View, SafeAreaView, Text, Image, TextInput, ScrollView, TouchableOpacity, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const Inicio = () => {
  const navigation = useNavigation();
  const [search, setSearch] = useState('');

  const posts = [
    {
      id: 1,
      imagem: require('../../../../assets/images/capaPost.png'),
      titulo: 'A Importância da Leitura na Formação dos Alunos',
      conteudo: 'A leitura é uma das habilidades mais essenciais que uma criança pode desenvolver ao longo de sua educação.',
      data: '11/10/2024',
      autor: 'Professor André',
    },
    // outros posts omitidos por brevidade
  ];

  const handlePress = (post) => {
    navigation.navigate('Post', {
      titulo: post.titulo,
      conteudo: post.conteudo,
      data: post.data,
      autor: post.autor,
    });
  };

  return (
    <SafeAreaView style={styles.safeArea}>
      <View style={styles.headerContainerFullWidth}>
        <View style={styles.headerFullWidthNoMargin}>
          <Image style={styles.img} source={require('../../../assets/images/logo.png')} />
          <Text style={styles.tituloPagina}>Bem Vindo, Professor</Text>
        </View>
        <View style={styles.barraPesquisa}>
          <Image style={styles.imgPesquisa} source={require('../../../assets/icons/search.png')} />
          <TextInput
            placeholder="Pesquisar"
            placeholderTextColor="#999"
            style={styles.inputPesquisa}
            value={search}
            onChangeText={setSearch}
          />
        </View>
      </View>

      <ScrollView contentContainerStyle={styles.scrollContainer}>
        {posts.map((post, index) => (
          <TouchableOpacity key={index} onPress={() => handlePress(post)} style={styles.postContainer}>
            <Image style={styles.capaPost} source={post.imagem} />
            <View style={styles.infoGeral}>
              <Text style={styles.tituloPost}>{post.titulo}</Text>
              <Text style={styles.conteudoPost}>{post.conteudo}</Text>
              <View style={styles.infoPost}>
                <Text style={styles.infoPostText}>{post.data}</Text>
                <Text style={styles.infoPostText}>{post.autor}</Text>
              </View>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  // estilos mantidos iguais aos fornecidos
});

export default Inicio;
