// app/navigation/NavegacaoTab.jsx
import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Inicio from '../screens/Home/Inicio';
import Postagens from '../screens/Home/Postagens';
import ListaProfessores from '../screens/Home/ListaProfessores';
import Perfil from '../screens/Home/Perfil/Perfil';
import Entrar from '../screens/Home/Entrar';
import Admin from '../screens/Admin/Admin';
import CriarPostagem from '../screens/Admin/CriarPostagem';
import TabBarIcon from '../../components/TabBar/TabIcon';

const Tab = createBottomTabNavigator();

export default function NavegacaoTab({ user = {} }) {
  // Garantir que o user não é undefined
  if (!user) {
    return null;
  }

  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: false,
        tabBarShowLabel: false,
        tabBarActiveTintColor: '#FBED51',
        tabBarInactiveTintColor: '#fff',
        tabBarStyle: {
          backgroundColor: '#2579E7',
          borderTopWidth: 1,
          borderTopColor: '#2579E7',
          height: 55, // Reduzir a altura da barra
        },
      }}
    >
      <Tab.Screen
        name="Inicio"
        component={Inicio}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/home.png')}
              color={color}
              focused={focused}
            />
          ),
        }}
      />
      <Tab.Screen
        name="Postagens"
        component={Postagens}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/posts.png')}
              color={color}
              focused={focused}
            />
          ),
        }}
      />
      <Tab.Screen
        name="ListaProfessores"
        component={ListaProfessores}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/users.png')}
              color={color}
              focused={focused}
            />
          ),
        }}
      />
      <Tab.Screen
        name="Perfil"
        component={Perfil}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/profile.png')}
              color={color}
              focused={focused}
            />
          ),
        }}
      />
      <Tab.Screen
        name="Entrar"
        component={Entrar}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/log-in.png')}
              color={color}
              focused={focused}
            />
          ),
        }}
      />
      {/* Aba disponível apenas para professores logados */}
      {user?.isProfessor && (
        <>
          <Tab.Screen
            name="Admin"
            component={Admin}
            options={{
              tabBarIcon: ({ color, focused }) => (
                <TabBarIcon
                  icon={require('../../assets/icons/user-check.png')}
                  color={color}
                  focused={focused}
                />
              ),
            }}
          />
          <Tab.Screen
            name="CriarPostagem"
            component={CriarPostagem}
            options={{
              tabBarIcon: ({ color, focused }) => (
                <TabBarIcon
                  icon={require('../../assets/icons/user-plus.png')}
                  color={color}
                  focused={focused}
                />
              ),
            }}
          />
        </>
      )}
    </Tab.Navigator>
  );
}
