// app/navigation/NavegacaoTab.jsx
import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Inicio from '../screens/Home/Inicio';
import Postagens from '../screens/Home/Postagens';
import ListaProfessores from '../screens/Home/ListaProfessores';
import Perfil from '../screens/Home/Perfil/Perfil';
import Entrar from '../screens/Home/Entrar';
import TabBarIcon from '../../components/TabBar/TabIcon';

const Tab = createBottomTabNavigator();

export default function NavegacaoTab() {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: false,
        tabBarShowLabel: true,
        tabBarActiveTintColor: '#FBED51',
        tabBarInactiveTintColor: '#fff',
        tabBarStyle: {
          backgroundColor: '#2579E7',
          borderTopWidth: 1,
          borderTopColor: '#2579E7',
          height: 55, // Barra mais fina para melhorar o visual
          paddingBottom: 5, // Ajuste para centralização dos ícones e labels
        },
        tabBarLabelStyle: {
          fontSize: 12,
          fontWeight: 'bold',
          marginBottom: 4,
        },
        tabBarIconStyle: {
          alignSelf: 'center',
        },
      }}
    >
      <Tab.Screen
        name="Inicio"
        component={Inicio}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/home.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Home',
        }}
      />
      <Tab.Screen
        name="Postagens"
        component={Postagens}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/posts.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Postagens',
        }}
      />
      <Tab.Screen
        name="ListaProfessores"
        component={ListaProfessores}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/users.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Professores',
        }}
      />
      <Tab.Screen
        name="Perfil"
        component={Perfil}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/profile.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Perfil',
        }}
      />
      <Tab.Screen
        name="Entrar"
        component={Entrar}
        options={{
          tabBarIcon: ({ color, focused }) => (
            <TabBarIcon
              icon={require('../../assets/icons/log-in.png')}
              color={color}
              focused={focused}
            />
          ),
          tabBarLabel: 'Login',
        }}
      />
    </Tab.Navigator>
  );
}
